options.script:
let x1  = 0;
let y1  = 1;

let i=0
var seriex = [];
for (i = 0; i < data.series.length; i=i+2) 
{
    y1  = data.series[i].fields[1].values.buffer;
    x1  = data.series[i+1].fields[1].values.buffer;
    seriex[i/2] = {
    x : x1, 
    y : y1, 
    "line": {
    "with": 1
    },
    "mode": "lines+markers",
    "marker": {
    "size": 5
    },
    name : data.series[i].name.substring(0,4)
    }
} 

return {
    data : seriex,
    config : {
        displayModeBar: false
    }
}

